sys_script_include.script:
var DM_GitHubUtils = Class.create();
DM_GitHubUtils.prototype = {
    initialize: function() {
    },

	parseAppSysId: function(inputString) {
		// Regular expression to match a 32-character hexadecimal string
        var sysIdPattern = /[0-9a-fA-F]{32}/;
        var match = inputString.match(sysIdPattern);

        if (match) {
            return inputString;  // Return the matched sys_id
        } else {
            return null;  // Return null if no match is found
        }
	},

	checkAppInstall: function(sysId) {
		// Query sys_app table to see if an app is installed
		var appGR = new GlideRecord('sys_app');
		if (appGR.get(sysId)) {
			return true;
		} else {
			return false;
		}
	},

    type: 'DM_GitHubUtils'
};
